import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Menu extends Component {
  constructor() {
    super();
    this.state = {
      partyName: '',
      isInputVisible: false
    }
  }

  showAddPartyInput = () => {
    this.setState({ partyName: '' , isInputVisible: !this.state.isInputVisible })
  }

  handleChange = (e) => {
    this.setState({ [e.target.name] : e.target.value })
  }

  handleAddParty = () => {

  }

  createPartyList = () => {
    return this.props.parties.map(party => {
      return(
        <li>{party.partyName}</li>
      )
    })
  }

  render() {
    return(
      <aside className='menu__section'>
        <button onClick={this.showAddPartyInput}> Add A Party </button>
        {this.state.isInputVisible ? <div> <input type='text' name='partyName' value={this.state.partyName} onChange={this.handleChange}/> <button onClick={this.handleAddParty}> + </button> </div>: ''}
        <li>{this.createPartyList()}</li>
      </aside>
    )
  }
}

export default connect(null, null)(Menu);
