import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Menu extends Component {
  constructor() {
    super();
    this.state = {
      parties: [],
      isInputVisible: false
    }
  }

  showAddPartyInput = () => {
    this.setState({ parties: [] , isInputVisible: !this.state.isInputVisible })
  }

  render() {
    return(
      <aside className='menu__section'>
        <button onClick={this.showAddPartyInput}> Add A Party </button>
        {this.state.isInputVisible ? <div> <input /> <button onClick={this.handleAddParty}> + </button> </div>: ''}
        <li>party 1</li>
      </aside>
    )
  }
}

export default connect(null, null)(Menu);
