import React, { Component } from 'react';
import { connect } from 'react-redux';

export class Menu extends Component {
  constructor() {
    super();
    this.state = {
      parties: []
    }
  }

  handleAddParty = () => {
    this.setState({ parties: [e.target.value]})
  }

  render() {
    return(
      <aside className='menu__section'>
        <button onClick={this.handleAddParty}> Add A Party</button>
        <li>party 1</li>
      </aside>
    )
  }
}

export default connect(null, null)(Menu);
