      partyName: '',
      isInputVisible: false
    }
  }

  showAddPartyInput = () => {
    this.setState({ partyName: '' , isInputVisible: !this.state.isInputVisible })
  }

  handleAddParty = () => {
    this.setState({ parties: [], isInputVisible: !this.state.isInputVisible })
  }

  handleChange = () => {

  }

  render() {
    return(
      <aside className='menu__section'>
        <button onClick={this.showAddPartyInput}> Add A Party </button>
        {this.state.isInputVisible ? <div> <input type='text' name='parties' value={this.state.partyName} onChange={this.handleChange}/> <button onClick={this.handleAddParty}> + </button> </div>: ''}
